import React from "react";
import { Sliders } from 'lucide-react';

const Theme = () => {
  return (  
    <div className="bg-white shadow-lg shadow-white-500/50 rounded-sm mt-3 ml-3 text-black flex items-center h-20 text-center max-xl:mr-3 max-xl:h-fit">
      <div className="ml-3">
        <h1 className="text-xl font-medium max-xl:text-6xl">Theme</h1>
        <p className="max-xl:text-4xl">Default</p>
      </div>
      <div className="flex items-center">
        <Sliders className="ml-9 max-xl:ml-80 max-xl:h-12 max-xl:w-12" color="black" size={20}/>
        <select className="bg-white hover:bg-gray-300 ml-3 text-xl max-xl:text-5xl max-xl:w-10 min-w-[20rem]">
          <option className="max-xl:text-xl">Change Theme</option>
          <option className="max-xl:text-xl">Default</option>
          <option className="max-xl:text-xl">Dark</option>
        </select>
      </div>
    </div>
  );
};

export default Theme;
