import React from "react";
import { Sliders } from 'lucide-react';

const Theme = () => {
  return (  
    <div className="bg-white shadow-lg shadow-white-500/50 rounded-sm mt-3 ml-3 text-black flex items-center h-20 text-center max-xl:mr-3 max-xl:h-fit">
      <div className="ml-3">
        <h1 className="text-xl font-medium max-xl:text-6xl">Theme</h1>
        <p className="max-xl:text-4xl">Default</p>
      </div>
      {/* <div className="flex items-center ml-auto mr-3">
        <Sliders className="ml-9 max-xl:ml-10 max-xl:h-12 max-xl:w-12" color="black" size={20}/>
        <select className="bg-white hover:bg-gray-300 ml-3 text-xl max-xl:text-5xl max-xl:w-fit">
          <option className="max-xl:text-xs">Change Theme</option>
          <option className="max-xl:text-xs">Default</option>
          <option className="max-xl:text-xs">Dark</option>
        </select> */}
        <div class="py-1" role="none">
      {/* <!-- Active: "bg-gray-100 text-gray-900", Not Active: "text-gray-700" --> */}
      <a href="#" class="block px-4 py-2 text-sm text-gray-700" role="menuitem" tabindex="-1" id="menu-item-0">Account settings</a>
      <a href="#" class="block px-4 py-2 text-sm text-gray-700" role="menuitem" tabindex="-1" id="menu-item-1">Support</a>
      <a href="#" class="block px-4 py-2 text-sm text-gray-700" role="menuitem" tabindex="-1" id="menu-item-2">License</a>
      </div>
    </div>
  );
};

export default Theme;
