import React from "react";
import Theme from "./Theme";
import Profile from "./Profile";
import Social_Media from "./Social_Media";
import Tech_Stack from "./Tech_Stack";
import Certification from "./Certification";
import Education from "./Education";
import Publication from "./Projects/Publication";
import Projects from "./Projects/Projects";
import Ongoing_project from "./Projects/Ongoing_project";
import { Calculator, Clock, Dumbbell, FileLock, Landmark, Monitor, MonitorCheck, RectangleEllipsis, SquarePlus } from 'lucide-react';
import Copy_right from "./Projects/Copy_right";
import { useTheme } from "./ThemeContext";

const Card = () => {
  const { theme, setTheme } = useTheme();

  // Determine global background and text color based on the selected theme
  const globalBackgroundColor = theme === 'Dark' ? 'bg-black' : 'bg-white';
  const globalTextColor = theme === 'Dark' ? 'text-white' : 'text-black';

  return (
    <div className={`${globalBackgroundColor} ${globalTextColor} min-h-screen`}>
      <div className={`w-full h-full text-black flex flex-col xl:flex-row rounded-sm`}>
        <div className="w-full xl:w-1/4 mb-3">
          <Theme theme={theme} setTheme={setTheme}/>
          <Profile setTheme={setTheme}/>
          <Social_Media />
          <Tech_Stack />
          <Certification />
          <Education />
        </div>
        <div className="w-full xl:w-3/4 shadow-lg shadow-white-500/50 rounded-sm mt-3 mb-3 mr-3 xl:ml-3 text-black font-bold">
          {/* The content of Card which should remain unaffected by the global theme */}
          <div className="flex flex-col bg-[#e8e8e8] rounded-sm max-xl:ml-3 max-xl:mr-3 max-xl:-mt-3">
            <div className="flex">
              <span className="ml-3 mt-3 text-2xl text-black font-medium max-xl:text-6xl max-xl:font-semibold">
                Github Projects
              </span>
              <span className="ml-auto font-light mr-3 mt-3">
                <a className="hover:underline hover:font-normal max-xl:text-3xl" href="">
                  See All
                </a>
              </span>
            </div>
            <div className="flex flex-wrap items-stretch">
              <div className="w-full xl:w-1/2">
                <Projects
                  title="Bank Management System"
                  description="In this project there is 3 panels Admin, Employee, Customer the Admin, Employee can perform CURD operation on Customer record."
                  technology="Java(swing), SQL"
                  logo={<Landmark size={15} className='max-xl:h-9 max-xl:w-9'/>}
                />
              </div>
              <div className="w-full xl:w-1/2">
                <Projects />
              </div>
            </div>
            {/* Other project components */}
          </div>
          <div className="flex flex-col mt-3 bg-[#e8e8e8] rounded-sm max-xl:ml-3 max-xl:mr-3">
            <div className="mt-3 ml-3">
              <Publication />
            </div>
          </div>
          <div className="flex flex-col mt-3 bg-[#e8e8e8] rounded-sm max-xl:ml-3 max-xl:mr-3">
            <div className="mt-3 ml-3">
              <Ongoing_project />
            </div>
          </div>
          <div className="flex mt-3 bg-slate-600 rounded-sm">
            <Copy_right />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Card;
