import React from "react";
import { Sliders } from 'lucide-react';

const Theme = () => {
  return (
    <div className="bg-white shadow-lg shadow-white-500/50 rounded-sm mt-3 ml-3 text-black flex items-center h-20 text-center 2xl:w-full md:justify-between md:p-3 lg:p-5">
      <div className="ml-3 flex-1">
        <h1 className="text-xl font-medium">Theme</h1>
        <p>Default</p>
      </div>
      <div className="flex items-center mr-3">
        <Sliders className="ml-3 md:ml-6 lg:ml-9" color="black" size={20} />
        <select className="bg-white hover:bg-gray-300 ml-3 p-2 border rounded-md">
          <option>Change Theme</option>
          <option>Default</option>
          <option>Dark</option>
        </select>
      </div>
    </div>
  );
};

export default Theme;
