// import React from "react";
import { Sliders } from 'lucide-react';
import { Menu, MenuButton, MenuItem, MenuItems } from '@headlessui/react'
import { ChevronDownIcon } from '@heroicons/react/20/solid'
const Theme = () => {
  return (  
    // <div className="bg-white shadow-lg shadow-white-500/50 rounded-sm mt-3 ml-3 text-black flex items-center h-20 text-center max-xl:mr-3 max-xl:h-fit">
    //   <div className="ml-3">
    //     <h1 className="text-xl font-medium max-xl:text-6xl">Theme</h1>
    //     <p className="max-xl:text-4xl">Default</p>
    //   </div>
    //   <div className="flex items-center ml-auto mr-3">
    //     <Sliders className="ml-9 max-xl:ml-10 max-xl:h-12 max-xl:w-12" color="black" size={20}/>
    //     <select className="bg-white hover:bg-gray-300 ml-3 text-xl max-xl:text-5xl max-xl:w-fit">
    //       <option className="max-xl:text-xs">Change Theme</option>
    //       <option className="max-xl:text-xs">Default</option>
    //       <option className="max-xl:text-xs">Dark</option>
    //     </select>
    //   </div>
    // </div>
    <Menu as="div" className="relative inline-block text-left">
      <div>
        <MenuButton className="inline-flex w-full justify-center gap-x-1.5 rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50">
          Options
          <ChevronDownIcon aria-hidden="true" className="-mr-1 h-5 w-5 text-gray-400" />
        </MenuButton>
      </div>

      <MenuItems
        transition
        className="absolute right-0 z-10 mt-2 w-56 origin-top-right rounded-md bg-white shadow-lg ring-1 ring-black ring-opacity-5 transition focus:outline-none data-[closed]:scale-95 data-[closed]:transform data-[closed]:opacity-0 data-[enter]:duration-100 data-[leave]:duration-75 data-[enter]:ease-out data-[leave]:ease-in"
      >
        <div className="py-1">
          <MenuItem>
            <a
              href="#"
              className="block px-4 py-2 text-sm text-gray-700 data-[focus]:bg-gray-100 data-[focus]:text-gray-900"
            >
              Account settings
            </a>
          </MenuItem>
          <MenuItem>
            <a
              href="#"
              className="block px-4 py-2 text-sm text-gray-700 data-[focus]:bg-gray-100 data-[focus]:text-gray-900"
            >
              Support
            </a>
          </MenuItem>
          <MenuItem>
            <a
              href="#"
              className="block px-4 py-2 text-sm text-gray-700 data-[focus]:bg-gray-100 data-[focus]:text-gray-900"
            >
              License
            </a>
          </MenuItem>
          <form action="#" method="POST">
            <MenuItem>
              <button
                type="submit"
                className="block w-full px-4 py-2 text-left text-sm text-gray-700 data-[focus]:bg-gray-100 data-[focus]:text-gray-900"
              >
                Sign out
              </button>
            </MenuItem>
          </form>
        </div>
      </MenuItems>
    </Menu>
  );
};

export default Theme;
